package pack; import java.util.Arrays; import java.util.HashSet; import java.util.Iterator; import java.util.Random; import java.util.Set; public class sets { public static int maxSet(Set s) { int max =0; Iterator it = s.iterator(); while(it.hasNext()) { Integer ele = (Integer)it.next(); if(ele>max) { max = ele; } } return max; } public static int minSet(Set s) { int min =0; Integer[] arr = new Integer[s.size()]; s.toArray(arr); Arrays.sort(arr); min = arr[0]; return min; } public static void main(String[] args) { Set<Integer> s = new HashSet<>(); Random r = new Random(); for(int i=0;i<10;i++) { s.add(r.nextInt(20, 50)); } System.out.println(s); System.out.println("The maximum element is : " + maxSet(s)); System.out.println("The minimum element is : " + minSet(s)); } }